def max_depth(s):
    depth = 0
    max_depth = 0

    for c in s:
        if c in '<':
            depth += 1
            max_depth = max(max_depth, depth)
        elif c in '>':
            depth -= 1
        # if depth > max_depth:
        #     max_depth = depth

    return max_depth

